import osfilename = input("Please enter the file name(string ex readme.txt).")cut = filename.split('.')[0]filetype = '.'+filename.split('.')[1]n = 0numbfile = open("numbfile","w")total=0i=0#count;check file existance while os.path.isfile(filename):    n=n+1    filename = cut+ str(n) + filetypenumbfile.write('There is a total of ' +str(n)+' data recorded.\n')while i in range(0,n):    filename = cut+filetype    fp = open(filename,'r+')    for line in fp.readlines():        if '@   xaxis label ' in line:            beginning = line.find('= ')+2            ending = line.find('eV')-1            numb = line[beginning:ending]                         else:            pass    i = i+1    numbfile.write(str(i)+' '+numb +'\n')    filename = cut+ str(i) + filetype    fp.close()    #Average    total = total + float(numb)avg = total / nnumbfile.close()